//random()
let r, g, b, x, y;

let circleSize;
let lineThick;

let squareSize;
let lineWidth;

let messages = ["You are doing great!", "Don't let it kick your butt!", "Nothing but a plot twist", "This too shall pass."];
let randomIndex;



var movement = 2;

function setup(){
  createCanvas(windowWidth, windowHeight);
  background(0);
  textSize(75);
  textAlign(CENTER,CENTER);

    // Set initial random index
  randomIndex = floor(random(messages.length));
  
}  
  
//draw circle
function draw(){

  // Display the current random message
text(messages[randomIndex], width / 2, height / 2);
  
r = random(255);
g = random(255);
b = random(255);
x = random(width);
y= random(height);
  
circleSize = random(5, 200); 
lineThick = random(1,10);
strokeWeight(lineThick);
stroke(0, 0,255,10);
fill(r, g, b, 100);
circle(x,y, circleSize);

  //draw square
squareSize = random(1, 200);
lineWidth = random(3, 25);
strokeWeight(lineWidth);
stroke(100, 0, 100, 10);
fill(r, g, b, 200);  
square(x, y, squareSize);
  
for (let i = r, g, b; i == random; i++) 
  ////wild cirle at top
  circle(x,y,10);

//random circles
function draw()
{  r = random(255);
g = random(255);
b = random(255);
x = random(width);
y= random(height);
  
circleSize = random(5, 200); 
lineThick = random(1,10);
strokeWeight(lineThick);
stroke(0, 0,255,10);
fill(r, g, b, 100);
circle(x,y, circleSize);

  if(x >= 800 || x <= 0)
    {
      movement*= -1;
      fill(r,g,b);
    }
    
  x += movement;

}  

function mousePressed() {
  // Change the message on mouse press
  randomIndex = floor(random(messages.length));
}
}  